import com.eviware.soapui.support.XmlHolder

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:clinicalprocess:healthcond:description:GetAlertInformationResponder:2'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:description:2'

def alertInformation = holder.getDomNodes('/soapenv:Envelope/soapenv:Body/urn:GetAlertInformationResponse/urn:alertInformation')

assert alertInformation.size() > 0

alertInformation.each {l ->
    def infoHolder = new XmlHolder( l )
    holder.namespaces.each {key, value -> infoHolder.namespaces[key] = value}

    def patientId = infoHolder.getNodeValue('/urn:alertInformation/urn1:alertInformationHeader/urn1:patientId/urn1:id')
    def patientIdType = infoHolder.getNodeValue('/urn:alertInformation/urn1:alertInformationHeader/urn1:patientId/urn1:type')

    assert patientId == context.patientId
    assert patientIdType == context.patientIdType
}

import com.eviware.soapui.support.XmlHolder

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:clinicalprocess:healthcond:description:GetAlertInformationResponder:2'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:description:2'

def alerts = holder.getDomNodes('/soapenv:Envelope/soapenv:Body/urn:GetAlertInformationResponse/urn:alertInformation')

if (alerts.length == 0) {
    log.error("Your testdata is incorrect, response contains no alert information.")
    assert(false)
}

def offending = []

alerts.each { alert ->
    def alertHolder = new XmlHolder( alert )
    holder.namespaces.each {key, value -> alertHolder.namespaces[key] = value}


    def documentId = alertHolder.getNodeValue('/urn:alertInformation/urn1:alertInformationHeader/urn1:documentId')
    def careUnitHSAId = alertHolder.getNodeValue('/urn:alertInformation/urn1:alertInformationHeader/urn1:accountableHealthcareProfessional/urn1:healthcareProfessionalCareUnitHSAId')

    if (careUnitHSAId != context.careUnitId1 && careUnitHSAId != context.careUnitId2) {
        offending.add("<documentId: ${documentId} careUnitHSAId: ${careUnitHSAId}>")
    }
}

if(!offending.isEmpty()) {
    log.error("Response contains alertInformation with unexpected CareUnitHSAId: ${offending}")
    assert(false)
}

