import com.eviware.soapui.support.XmlHolder
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:GetReferralOutcomeResponder:3'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:3'

def documents = holder.getDomNodes('/soapenv:Envelope/soapenv:Body/urn:GetReferralOutcomeResponse/urn:referralOutcome')

if (documents.length == 0) {
	contextHelper.fail("Response contains no documents.")
	return
}

documents.each { doc -> 
	def docHolder = new XmlHolder( doc )
	holder.namespaces.each {key, value -> docHolder.namespaces[key] = value}

	def documentId = docHolder.getNodeValue('/urn:referralOutcome/urn1:referralOutcomeHeader/urn1:documentId')
	def careUnitHSAid = docHolder.getNodeValue('/urn:referralOutcome/urn1:referralOutcomeHeader/urn1:accountableHealthcareProfessional/urn1:healthcareProfessionalCareUnitHSAId')

	if (careUnitHSAid != context.careUnitHSAid) {
		contextHelper.fail("Response contains a document with other CareUnitHSAid. CareUnitHSAid: ${careUnitHSAid} DocumentId: ${documentId}")
	}
}

